mapper.update:
UPDATE tb_emp_acct
   SET login_pwd = #{loginPwd},
       updt_dt   = SYSDATE,
       updt_by   = #{updtBy}
 WHERE emp_acct_id = #{empAcctId}

UPDATE tb_emp_acct
   SET temp_yn = 'e2',    
       updt_dt = SYSDATE,
       updt_by = #{updtBy}
 WHERE emp_acct_id = #{empAcctId}

mapper.select:
SELECT dept_id AS dept_id, dept_nm AS dept_nm FROM tb_dept WHERE 1 = 1
AND vend_id = #{vendId}
ORDER BY dept_nm

SELECT e.cttpc
  FROM TB_EMP e
 WHERE e.vend_id = #{vendId}
   AND e.emp_id  = #{empId}

mapper.update:
UPDATE tb_emp_acct
   SET st = #{st},
       updt_dt = SYSDATE,
       updt_by = #{updtBy}
 WHERE vend_id = #{vendId}
   AND st  <>  #{st}

UPDATE tb_emp_acct
   SET st      = 'r3',
       updt_dt = SYSDATE,
       updt_by = #{updtBy}
 WHERE vend_id = #{vendId}
   AND emp_id  = #{empId}

UPDATE tb_emp_acct
   SET st      = 'r1',
       updt_dt = SYSDATE,
       updt_by = #{updtBy}
 WHERE vend_id = #{vendId}
   AND emp_id  = #{empId}
   AND st      = 'r3'
