mapper.select:
 select 
 a.*
 ,b.nm "cfm_nm"
 ,c.nm
from tb_vcatn a
left join tb_emp b
on a.confmer_id = b.emp_id
left join tb_emp c
on a.emp_id = c.emp_id

  AND a.emp_id = #{empId}
  AND a.vcatn_begin >= #{vcatnBegin}
   AND a.vcatn_end <= #{vcatnEnd}  
  AND a.vcatn_ty = #{vcatnTy}
  AND a.st = #{st}

SELECT NVL(
    (
        SELECT remndr_yryc
        FROM tb_yryc
        WHERE emp_id = #{empId}
          AND vcatn_year = (
              SELECT MAX(vcatn_year)
              FROM tb_yryc
              WHERE emp_id = #{empId}
          )
    ), 0
) AS remndr_yryc
FROM dual

mapper.insert:
SELECT 'vcatn' || TO_CHAR(SYSDATE, 'YYMM') ||
       LPAD(NVL(MAX(SUBSTR(vcatn_id, LENGTH(vcatn_id)-2, 3)), 0) + 1, 3, '0') AS vcatn_id
FROM tb_vcatn
WHERE SUBSTR(vcatn_id, 6, 4) = TO_CHAR(SYSDATE, 'YYMM')

insert into tb_vcatn(
 vcatn_id
 ,emp_id
 ,vcatn_ty
 ,saly_pymnt_st
 ,vcatn_begin
 ,vcatn_end
 ,vcatn_resn
 ,st
 ,vcatn_de
 ,crea_by
)VALUES(
 #{vcatnId}
 ,#{empId}
 ,#{vcatnTy}
 ,#{salyPymntSt}
 ,#{vcatnBegin}
 ,#{vcatnEnd}
 ,#{vcatnResn}
 ,'g1'
 ,#{vcatnDe}
 ,'myk'
)

mapper.update:
UPDATE tb_yryc
SET use_yryc   = use_yryc + #{vcatnDe}
    ,remndr_yryc = remndr_yryc - #{vcatnDe}
    ,updt_dt = SYSDATE
    ,updt_by = 'myk'
WHERE emp_id = #{empId}
  AND vcatn_year = (
        SELECT MAX(vcatn_year)
        FROM tb_yryc
        WHERE emp_id = #{empId}
    )
    AND remndr_yryc >= #{vcatnDe}

UPDATE tb_yryc
SET use_yryc   = use_yryc - #{vcatnDe}
    ,remndr_yryc = remndr_yryc + #{vcatnDe}
    ,updt_dt = SYSDATE
    ,updt_by = 'myk'
WHERE emp_id = #{empId}
  AND vcatn_year = (
        SELECT MAX(vcatn_year)
        FROM tb_yryc
        WHERE emp_id = #{empId}
    )

UPDATE tb_vcatn
SET
 confmer_id = #{confmerId}
 ,st = #{st}
 ,rm = #{rm}
 ,return_resn = #{returnResn}
 ,updt_dt = SYSDATE
 ,updt_by = 'myk'
WHERE vcatn_id = #{vcatnId}
